import React, { useState, useEffect } from "react";
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from "react-icons/fa";
import "./slider.scss";

const HomeSlider = () => {
  const images = [
    {
      id: 0,
      title: "Pijamas",
      imageUrl: "images/uno.jpg",
    },
    {
      id: 1,
      title: "Batas",
      imageUrl: "images/dos.jpg",
    },
    {
      id: 2,
      title: "Pantuflas",
      imageUrl: "images/tres.jpg",
    },
    {
      id: 3,
      title: "Cojines",
      imageUrl: "images/cuatro.jpg",
    },
  ];

  const [current, setCurrent] = useState(0);
  const length = images.length;

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  const autoSlider =()=>{
      for (let index = 0; index > length; index++) {
          console.log(index, 'index here');

      }
  }
useEffect(() => {
    autoSlider()

}, [current])
  if (!Array.isArray(images) || images.length <= 0) {
    return null;
  }

  console.log(images, "here it is");
  return (
    <div className="slideContainer">
      <FaArrowAltCircleLeft className="left-arrow" onClick={prevSlide}/>
      <FaArrowAltCircleRight className="right-arrow" onClick={nextSlide}/>
      {images.map(({ id, title, imageUrl }) => {
        return (
          <div
            className={id === current ? "slide active" : "slide"}
            key={id}
          >
            {id === current && <img src={imageUrl} key={id} alt={title} />}
          </div>
        );
      })}
    </div>
  );
};

export default HomeSlider;
